import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

function Register() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    fullName: "",
    mobileNumber: "",
    email: "",
    gender: "",
    designation: "",
    licenseNumber: "",
    degrees: [""],
    dateOfBirth: "",
    password: "",
    confirmPassword: "",
  });

  const [error, setError] = useState("");
  const [fieldErrors, setFieldErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const baseURL = process.env.REACT_APP_BACK_END_BASE_URL || "http://localhost:8080";

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleDegreeChange = (index, value) => {
    const newDegrees = [...form.degrees];
    newDegrees[index] = value;
    setForm((prev) => ({ ...prev, degrees: newDegrees }));
  };

  const addDegree = () => {
    setForm((prev) => ({ ...prev, degrees: [...prev.degrees, ""] }));
  };

  const removeDegree = (index) => {
    if (form.degrees.length === 1) return;
    const newDegrees = form.degrees.filter((_, i) => i !== index);
    setForm((prev) => ({ ...prev, degrees: newDegrees }));
  };

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  const validateMobile = (mobile) => /^[0-9]{10,15}$/.test(mobile);

  const validateForm = () => {
    const errors = {};
    if (!form.fullName.trim()) errors.fullName = "Full Name is required.";
    if (!form.mobileNumber.trim()) errors.mobileNumber = "Mobile Number is required.";
    else if (!validateMobile(form.mobileNumber)) errors.mobileNumber = "Invalid Mobile Number.";

    if (!form.email.trim()) errors.email = "Email is required.";
    else if (!validateEmail(form.email)) errors.email = "Invalid Email.";

    if (!form.gender) errors.gender = "Gender is required.";
    if (!form.designation.trim()) errors.designation = "Designation is required.";
    if (!form.licenseNumber.trim()) errors.licenseNumber = "License Number is required.";

    if (!form.degrees.length || form.degrees.every((d) => !d.trim()))
      errors.degrees = "At least one degree is required.";
    else {
      form.degrees.forEach((deg, idx) => {
        if (!deg.trim()) errors[`degrees_${idx}`] = "Degree cannot be empty.";
      });
    }

    if (!form.dateOfBirth) errors.dateOfBirth = "Date of Birth is required.";
    if (!form.password) errors.password = "Password is required.";
    else if (form.password.length < 6) errors.password = "Minimum 6 characters.";

    if (!form.confirmPassword) errors.confirmPassword = "Confirm Password is required.";
    else if (form.password !== form.confirmPassword)
      errors.confirmPassword = "Passwords do not match.";

    setFieldErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleRegister = async () => {
    setError("");
    if (!validateForm()) return;

    setLoading(true);
    try {
      const response = await fetch(`${baseURL}/api/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...form,
          degrees: form.degrees.filter((d) => d.trim() !== ""),
        }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("ðŸŽ‰ Registration successful! Redirecting to login...");
        navigate("/login");
      } else {
        setError(data.message || "Registration failed.");
      }
    } catch (err) {
      setError("Server error. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <style>{`
        .register-page {
          background-color: #f9f9f9;
          min-height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          padding: 20px;
          font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
        }
        .register-card {
          width: 100%;
          max-width: 500px;
          background: white;
          padding: 30px;
          border-radius: 12px;
          box-shadow: 0 8px 24px rgba(0, 0, 0, 0.1);
        }
        .register-card h2 {
          text-align: center;
          margin-bottom: 24px;
          font-size: 26px;
          color: #2c3e50;
        }
        .form-label {
          font-weight: 600;
          color: #333;
          display: block;
          margin-bottom: 6px;
          margin-top: 12px;
        }
        .form-input {
          width: 100%;
          padding: 10px 14px;
          border: 1px solid #ccc;
          border-radius: 8px;
          font-size: 15px;
          margin-bottom: 10px;
          outline: none;
          transition: border 0.3s;
        }
        .form-input:focus {
          border-color: #007bff;
        }
        .error {
          color: #e74c3c;
          font-size: 13px;
          margin-top: -8px;
          margin-bottom: 10px;
        }
        .degree-row {
          display: flex;
          align-items: center;
          gap: 8px;
          margin-bottom: 8px;
        }
        .add-button {
          background-color: #ecf0f1;
          padding: 8px 12px;
          border-radius: 6px;
          border: 1px solid #bdc3c7;
          cursor: pointer;
          font-weight: 500;
          margin-bottom: 16px;
          transition: background-color 0.3s;
        }
        .add-button:hover {
          background-color: #d1d8de;
        }
        .remove-button {
          background-color: #e74c3c;
          color: white;
          border: none;
          border-radius: 50%;
          width: 28px;
          height: 28px;
          font-size: 18px;
          cursor: pointer;
          transition: background-color 0.3s;
        }
        .remove-button:hover {
          background-color: #c0392b;
        }
        .submit-button {
          width: 100%;
          padding: 12px;
          background-color: #007bff;
          color: white;
          font-size: 16px;
          font-weight: bold;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          margin-top: 10px;
          transition: background-color 0.3s;
        }
        .submit-button:disabled {
          background-color: #6c757d;
          cursor: not-allowed;
        }
        .submit-button:hover:not(:disabled) {
          background-color: #0056b3;
        }
        .login-link {
          text-align: center;
          margin-top: 16px;
          font-size: 14px;
        }
        .login-link a {
          color: #007bff;
          text-decoration: none;
          font-weight: 600;
        }
        .login-link a:hover {
          text-decoration: underline;
        }
      `}</style>

      <div className="register-page">
        <div className="register-card">
          <h2>Doctor Registration</h2>

          <Input label="Full Name" name="fullName" value={form.fullName} onChange={handleChange} error={fieldErrors.fullName} />
          <Input label="Mobile Number" name="mobileNumber" value={form.mobileNumber} onChange={handleChange} error={fieldErrors.mobileNumber} />
          <Input label="Email" name="email" value={form.email} onChange={handleChange} error={fieldErrors.email} />

          <label className="form-label">Gender *</label>
          <select name="gender" value={form.gender} onChange={handleChange} className="form-input">
            <option value="">-- Select Gender --</option>
            <option>Male</option>
            <option>Female</option>
            <option>Others</option>
          </select>
          {fieldErrors.gender && <p className="error">{fieldErrors.gender}</p>}

          <Input label="Designation" name="designation" value={form.designation} onChange={handleChange} error={fieldErrors.designation} />
          <Input label="License Number" name="licenseNumber" value={form.licenseNumber} onChange={handleChange} error={fieldErrors.licenseNumber} />

          <label className="form-label">Degrees *</label>
          {form.degrees.map((degree, idx) => (
            <div className="degree-row" key={idx}>
              <input
                type="text"
                placeholder={`Degree #${idx + 1}`}
                value={degree}
                onChange={(e) => handleDegreeChange(idx, e.target.value)}
                className="form-input"
              />
              {form.degrees.length > 1 && (
                <button type="button" className="remove-button" onClick={() => removeDegree(idx)}>&times;</button>
              )}
              {fieldErrors[`degrees_${idx}`] && <p className="error">{fieldErrors[`degrees_${idx}`]}</p>}
            </div>
          ))}
          {fieldErrors.degrees && <p className="error">{fieldErrors.degrees}</p>}

          <button type="button" className="add-button" onClick={addDegree}>+ Add Another Degree</button>

          <Input label="Date of Birth" name="dateOfBirth" type="date" value={form.dateOfBirth} onChange={handleChange} error={fieldErrors.dateOfBirth} />
          <Input label="Password" name="password" type="password" value={form.password} onChange={handleChange} error={fieldErrors.password} />
          <Input label="Confirm Password" name="confirmPassword" type="password" value={form.confirmPassword} onChange={handleChange} error={fieldErrors.confirmPassword} />

          {error && <p className="error">{error}</p>}

          <button onClick={handleRegister} className="submit-button" disabled={loading}>
            {loading ? "Registering..." : "Register"}
          </button>

          <p className="login-link">Already registered? <Link to="/login">Login here</Link></p>
        </div>
      </div>
    </>
  );
}

const Input = ({ label, name, value, onChange, error, type = "text" }) => (
  <>
    <label htmlFor={name} className="form-label">{label} *</label>
    <input
      id={name}
      name={name}
      type={type}
      value={value}
      onChange={onChange}
      className="form-input"
    />
    {error && <p className="error">{error}</p>}
  </>
);

export default Register;
