import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

function Register() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    fullName: "",
    mobileNumber: "",
    email: "",
    gender: "",
    designation: "",
    licenseNumber: "",
    degrees: [""], // start with one degree input
    dateOfBirth: "",
    password: "",
    confirmPassword: "",
  });

  const [error, setError] = useState("");
  const [fieldErrors, setFieldErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const baseURL = process.env.REACT_APP_BACK_END_BASE_URL || "";

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleDegreeChange = (index, value) => {
    const newDegrees = [...form.degrees];
    newDegrees[index] = value;
    setForm(prev => ({ ...prev, degrees: newDegrees }));
  };

  const addDegree = () => {
    setForm(prev => ({ ...prev, degrees: [...prev.degrees, ""] }));
  };

  const removeDegree = (index) => {
    if (form.degrees.length === 1) return; // at least one degree required
    const newDegrees = form.degrees.filter((_, i) => i !== index);
    setForm(prev => ({ ...prev, degrees: newDegrees }));
  };

  // Validation helpers
  const validateEmail = (email) =>
    /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  const validateMobile = (mobile) =>
    /^[0-9]{10,15}$/.test(mobile); // simple digit check, length 10-15

  const validateForm = () => {
    let errors = {};

    if (!form.fullName.trim()) errors.fullName = "Full Name is required.";
    if (!form.mobileNumber.trim()) errors.mobileNumber = "Mobile Number is required.";
    else if (!validateMobile(form.mobileNumber.trim()))
      errors.mobileNumber = "Mobile Number is invalid.";

    if (!form.email.trim()) errors.email = "Email is required.";
    else if (!validateEmail(form.email.trim())) errors.email = "Email is invalid.";

    if (!form.gender) errors.gender = "Gender is required.";

    if (!form.designation.trim()) errors.designation = "Designation is required.";

    if (!form.licenseNumber.trim()) errors.licenseNumber = "License Number is required.";

    // Degrees: at least one non-empty degree
    if (!form.degrees.length || form.degrees.every(d => !d.trim()))
      errors.degrees = "At least one degree is required.";
    else {
      form.degrees.forEach((deg, idx) => {
        if (!deg.trim()) {
          errors[`degrees_${idx}`] = "Degree cannot be empty.";
        }
      });
    }

    if (!form.dateOfBirth) errors.dateOfBirth = "Date of Birth is required.";

    if (!form.password) errors.password = "Password is required.";
    else if (form.password.length < 6) errors.password = "Password must be at least 6 characters.";

    if (!form.confirmPassword) errors.confirmPassword = "Confirm Password is required.";
    else if (form.password !== form.confirmPassword) errors.confirmPassword = "Passwords do not match.";

    setFieldErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleRegister = async () => {
    setError("");
    if (!validateForm()) return;

    setLoading(true);
    try {
      const response = await fetch(`${baseURL}/api/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          fullName: form.fullName,
          mobileNumber: form.mobileNumber,
          email: form.email,
          gender: form.gender,
          designation: form.designation,
          licenseNumber: form.licenseNumber,
          degrees: form.degrees.filter((d) => d.trim() !== ""),
          dateOfBirth: form.dateOfBirth,
          password: form.password,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate("/login");
      } else {
        setError(data.message || "Registration failed. Please try again.");
      }
    } catch (err) {
      setError("Registration failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card} role="main" aria-label="Register form">
        <h2 style={styles.title}>Create Account</h2>

        {/* Full Name */}
        <label htmlFor="fullName" style={styles.label}>Full Name *</label>
        <input
          id="fullName"
          name="fullName"
          type="text"
          placeholder="Enter your full name"
          value={form.fullName}
          onChange={handleChange}
          style={styles.input}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.fullName}
        />
        {fieldErrors.fullName && <p style={styles.error}>{fieldErrors.fullName}</p>}

        {/* Mobile Number */}
        <label htmlFor="mobileNumber" style={styles.label}>Mobile Number *</label>
        <input
          id="mobileNumber"
          name="mobileNumber"
          type="tel"
          placeholder="Enter your mobile number"
          value={form.mobileNumber}
          onChange={handleChange}
          style={styles.input}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.mobileNumber}
        />
        {fieldErrors.mobileNumber && <p style={styles.error}>{fieldErrors.mobileNumber}</p>}

        {/* Email */}
        <label htmlFor="email" style={styles.label}>Email *</label>
        <input
          id="email"
          name="email"
          type="email"
          placeholder="Enter your email"
          value={form.email}
          onChange={handleChange}
          style={styles.input}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.email}
        />
        {fieldErrors.email && <p style={styles.error}>{fieldErrors.email}</p>}

        {/* Gender */}
        <label htmlFor="gender" style={styles.label}>Gender *</label>
        <select
          id="gender"
          name="gender"
          value={form.gender}
          onChange={handleChange}
          style={styles.select}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.gender}
        >
          <option value="">-- Select Gender --</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Others">Others</option>
        </select>
        {fieldErrors.gender && <p style={styles.error}>{fieldErrors.gender}</p>}

        {/* Designation */}
        <label htmlFor="designation" style={styles.label}>Designation *</label>
        <input
          id="designation"
          name="designation"
          type="text"
          placeholder="Enter your designation"
          value={form.designation}
          onChange={handleChange}
          style={styles.input}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.designation}
        />
        {fieldErrors.designation && <p style={styles.error}>{fieldErrors.designation}</p>}

        {/* License Number */}
        <label htmlFor="licenseNumber" style={styles.label}>License Number *</label>
        <input
          id="licenseNumber"
          name="licenseNumber"
          type="text"
          placeholder="Enter your license number"
          value={form.licenseNumber}
          onChange={handleChange}
          style={styles.input}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.licenseNumber}
        />
        {fieldErrors.licenseNumber && <p style={styles.error}>{fieldErrors.licenseNumber}</p>}

        {/* Degrees */}
        <label style={styles.label}>Degrees *</label>
        {form.degrees.map((degree, idx) => (
          <div key={idx} style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
            <input
              type="text"
              placeholder={`Degree #${idx + 1}`}
              value={degree}
              onChange={(e) => handleDegreeChange(idx, e.target.value)}
              style={{ ...styles.input, flex: 1, marginBottom: 0 }}
              disabled={loading}
              aria-required="true"
              aria-invalid={!!fieldErrors[`degrees_${idx}`]}
            />
            {form.degrees.length > 1 && (
              <button
                type="button"
                onClick={() => removeDegree(idx)}
                disabled={loading}
                aria-label={`Remove degree #${idx + 1}`}
                style={styles.removeButton}
              >
                &times;
              </button>
            )}
          </div>
        ))}
        {fieldErrors.degrees && <p style={styles.error}>{fieldErrors.degrees}</p>}
        {form.degrees.some((_, idx) => fieldErrors[`degrees_${idx}`]) &&
          form.degrees.map((_, idx) =>
            fieldErrors[`degrees_${idx}`] ? (
              <p key={`deg_err_${idx}`} style={styles.error}>
                {fieldErrors[`degrees_${idx}`]}
              </p>
            ) : null
          )}

        <button
          type="button"
          onClick={addDegree}
          disabled={loading}
          style={styles.addDegreeButton}
        >
          + Add Another Degree
        </button>

        {/* Date of Birth */}
        <label htmlFor="dateOfBirth" style={styles.label}>Date of Birth *</label>
        <input
          id="dateOfBirth"
          name="dateOfBirth"
          type="date"
          value={form.dateOfBirth}
          onChange={handleChange}
          style={styles.input}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.dateOfBirth}
        />
        {fieldErrors.dateOfBirth && <p style={styles.error}>{fieldErrors.dateOfBirth}</p>}

        {/* Password */}
        <label htmlFor="password" style={styles.label}>Password *</label>
        <input
          id="password"
          name="password"
          type="password"
          placeholder="Enter your password"
          value={form.password}
          onChange={handleChange}
          style={styles.input}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.password}
        />
        {fieldErrors.password && <p style={styles.error}>{fieldErrors.password}</p>}

        {/* Confirm Password */}
        <label htmlFor="confirmPassword" style={styles.label}>Confirm Password *</label>
        <input
          id="confirmPassword"
          name="confirmPassword"
          type="password"
          placeholder="Confirm your password"
          value={form.confirmPassword}
          onChange={handleChange}
          style={styles.input}
          disabled={loading}
          aria-required="true"
          aria-invalid={!!fieldErrors.confirmPassword}
        />
        {fieldErrors.confirmPassword && <p style={styles.error}>{fieldErrors.confirmPassword}</p>}

        {/* General error */}
        {error && <p style={styles.error}>{error}</p>}

        {/* Submit */}
        <button
          onClick={handleRegister}
          style={{ ...styles.button, ...(loading ? styles.buttonDisabled : {}) }}
          disabled={loading}
          aria-busy={loading}
          type="button"
        >
          {loading ? "Registering..." : "Register"}
        </button>

        <p style={styles.loginText}>
          Already have an account?{" "}
          <Link to="/login" style={styles.loginLink}>
            Login here
          </Link>
        </p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    minHeight: "100vh",
    background:
      "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: "20px",
  },
  card: {
    backgroundColor: "white",
    padding: "40px 50px",
    borderRadius: "12px",
    boxShadow: "0 8px 20px rgba(0,0,0,0.15)",
    width: "400px",
    maxWidth: "100%",
    textAlign: "left",
    overflowY: "auto",
    maxHeight: "90vh",
  },
  title: {
    marginBottom: "24px",
    color: "#333",
    textAlign: "center",
    fontWeight: "700",
  },
  label: {
    display: "block",
    marginBottom: "6px",
    fontWeight: "600",
    color: "#555",
  },
  input: {
    width: "100%",
    padding: "12px 15px",
    marginBottom: "16px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "16px",
    outline: "none",
    transition: "border-color 0.3s",
    boxSizing: "border-box",
  },
  select: {
    width: "100%",
    padding: "12px 15px",
    marginBottom: "16px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "16px",
    outline: "none",
    boxSizing: "border-box",
  },
  error: {
    color: "#e74c3c",
    marginTop: "-12px",
    marginBottom: "16px",
    fontWeight: "600",
    fontSize: "14px",
  },
  button: {
    width: "100%",
    padding: "12px 15px",
    backgroundColor: "#667eea",
    color: "white",
    fontWeight: "600",
    fontSize: "16px",
    borderRadius: "8px",
    border: "none",
    cursor: "pointer",
    transition: "background-color 0.3s",
  },
  buttonDisabled: {
    backgroundColor: "#a2a6f4",
    cursor: "not-allowed",
  },
  loginText: {
    marginTop: "20px",
    fontSize: "14px",
    textAlign: "center",
    color: "#555",
  },
  loginLink: {
    color: "#667eea",
    fontWeight: "600",
    textDecoration: "none",
  },
  addDegreeButton: {
    marginBottom: "24px",
    padding: "8px 15px",
    backgroundColor: "#eee",
    borderRadius: "6px",
    border: "1px solid #ccc",
    cursor: "pointer",
    fontWeight: "600",
  },
  removeButton: {
    marginLeft: "8px",
    backgroundColor: "#e74c3c",
    border: "none",
    color: "white",
    borderRadius: "50%",
    width: "28px",
    height: "28px",
    cursor: "pointer",
    fontSize: "18px",
    lineHeight: "1",
  },
};

export default Register;
