import { useState, useContext } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { AuthContext } from "./AuthContext";
import NavBar from "../NavBar_Footer/NavBarCus";

const Login = () => {
  const { login, darkMode } = useContext(AuthContext);
  const navigate = useNavigate();

  const [credentials, setCredentials] = useState({ username: "", password: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const baseURL = process.env.REACT_APP_BACK_END_BASE_URL;

  // Generic input change handler
  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const response = await fetch(`${baseURL}/api/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials),
      });

      const data = await response.json();

      if (response.ok && data.token) {
        login(data.token);
        navigate("/");
      } else {
        setError(data.message || "Invalid username or password.");
      }
    } catch {
      setError("Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // Dynamic classes helper
  const inputClass = `w-full px-4 py-3 border rounded-lg shadow-sm focus:outline-none focus:ring-2 ${
    darkMode
      ? "bg-gray-700 border-gray-600 text-white focus:ring-blue-400"
      : "bg-white border-gray-300 text-gray-900 focus:ring-blue-500"
  }`;

  const buttonClass = `w-full py-3 text-white font-semibold rounded-lg transition duration-300 ${
    loading ? "bg-gray-500 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
  }`;

  const containerClass = `min-h-screen flex items-center justify-center px-4 transition-all duration-300 ${
    darkMode ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900"
  }`;

  const cardClass = `max-w-md w-full p-8 rounded-2xl shadow-lg transition-all duration-300 ${
    darkMode ? "bg-gray-800" : "bg-white"
  }`;

  return (
    <>
      <NavBar />
      <div className={containerClass}>
        <main className={cardClass} aria-label="Login form">
          <h2 className="text-center text-3xl font-extrabold">Welcome Back!</h2>
          <p className="text-center mt-2">Sign in to continue</p>

          {error && <p className="text-center text-red-500 mt-4">{error}</p>}

          <form onSubmit={handleLogin} className="mt-6 space-y-6" noValidate>
            <div>
              <label htmlFor="username" className="block text-sm font-medium">
                Username
              </label>
              <input
                id="username"
                name="username"
                type="text"
                value={credentials.username}
                onChange={handleChange}
                className={inputClass}
                placeholder="Enter your username"
                required
                disabled={loading}
                autoComplete="username"
                aria-required="true"
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium">
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                value={credentials.password}
                onChange={handleChange}
                className={inputClass}
                placeholder="Enter your password"
                required
                disabled={loading}
                autoComplete="current-password"
                aria-required="true"
              />
            </div>

            <button
              type="submit"
              className={buttonClass}
              disabled={loading}
              aria-busy={loading}
            >
              {loading ? "Logging in..." : "Login"}
            </button>
          </form>

          <p className="text-center mt-6 text-sm">
            Don't have an account?{" "}
            <NavLink
              to="/register"
              className="font-medium text-blue-500 hover:text-blue-600"
            >
              Register here
            </NavLink>
          </p>
        </main>
      </div>
    </>
  );
};

export default Login;
