import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Reports = () => {
  const [reports, setReports] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const reportsPerPage = 5;
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  // Replace with actual token retrieval logic (localStorage/sessionStorage/etc.)
  const token = localStorage.getItem('token'); // Example
  const baseURL= process.env.REACT_APP_BACK_END_BASE_URL || "";

  useEffect(() => {
    setLoading(true);
    axios.get(`${baseURL}/api/prescription/report`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then(response => {
        setReports(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to load reports.');
        setLoading(false);
      });
  }, [token]);

  const indexOfLastReport = currentPage * reportsPerPage;
  const indexOfFirstReport = indexOfLastReport - reportsPerPage;
  const currentReports = reports.slice(indexOfFirstReport, indexOfLastReport);
  const totalPages = Math.ceil(reports.length / reportsPerPage);

  const handlePrev = () => {
    if (currentPage > 1) setCurrentPage(prev => prev - 1);
  };

  const handleNext = () => {
    if (currentPage < totalPages) setCurrentPage(prev => prev + 1);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Prescription Report</h2>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {!loading && !error && (
        <>
          <table border="1" cellPadding="10" cellSpacing="0" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Date</th>
                <th>Prescription Count</th>
              </tr>
            </thead>
            <tbody>
              {currentReports.length === 0 ? (
                <tr>
                  <td colSpan="2" style={{ textAlign: 'center' }}>No data available</td>
                </tr>
              ) : (
                currentReports.map((report, index) => (
                  <tr key={index}>
                    <td>{report.day}</td>
                    <td>{report.count}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>

          <div style={{ marginTop: '20px', display: 'flex', justifyContent: 'center', gap: '10px' }}>
            <button onClick={handlePrev} disabled={currentPage === 1}>Previous</button>
            <span>Page {currentPage} of {totalPages}</span>
            <button onClick={handleNext} disabled={currentPage === totalPages}>Next</button>
          </div>
        </>
      )}
    </div>
  );
};

export default Reports;
